using namespace std;char temp[100][100];int main() {int r, c;int a, b;cin >> r >> c;for (int i = 0; i < r; i++) cin >> temp[i];cin >> a >> b;for (int i = 0; i < r; i++) {for (int j = 0; j < c; j++) {temp[i][2 * c - 1 - j] = temp[i][j];}}for (int i = 0; i < c; i++) {for (int j = 0; j < r; j++) {	temp[2 * r - 1 - j][i] = temp[j][i];}}for (int i = r - 1; i < 2 * r; i++) {	for (int j = 0; j < c; j++) {	temp[i][2*c - 1 - j] = temp[i][j];}}if (temp[a - 1][b - 1] == '.') temp[a - 1][b - 1] = '#';else if(temp[a - 1][b - 1] == '#') temp[a - 1][b - 1] = '.';for (int i = 0; i < 2 * r; i++) {	for (int j = 0; j < 2 * c; j++) {	cout << temp[i][j];}	cout << endl;}}